#!/usr/local/bin/python# -*- coding: utf-8 -*-from ..utils.pandas_util import PandasUtilclass ManagerREIT:    """        Class responsible for keep the market information updated in internal data base    """    @staticmethod    def load_info_into_dataframe(csv_file):        """            Based on .csv normalize only relevant information and add to memory dataframe.        :return:            reits_df        """        funds_cad_df = PandasUtil.read_file_csv(csv_file, encoding='ISO-8859-1',                                                usecols=['CNPJ_FUNDO', 'DENOM_SOCIAL', 'DT_REG', 'CD_CVM', 'SIT',                                                         'TP_FUNDO', 'VL_PATRIM_LIQ'])        funds_cad_df = funds_cad_df.loc[funds_cad_df['SIT'] == 'EM FUNCIONAMENTO NORMAL']        funds_cad_df = funds_cad_df.loc[funds_cad_df['TP_FUNDO'] == 'FII']        # dropping ALL duplicated values        funds_cad_df.drop_duplicates(subset='CNPJ_FUNDO', keep=False, inplace=True)        funds_cad_df = funds_cad_df.reset_index(drop=True)        return funds_cad_df